mapper.update:
UPDATE BOARD SET TITLE = #{title}, CONTENTS = #{contents},
FILE_NM = #{file_nm}, ADD_FILE = #{add_file},
UPDATER_ID = #{user_id}, UPDATE_DATE = SYSDATE WHERE BOARD_NO = #{board_no}

mapper.select:
SELECT ADD_FILE, FILE_NM
FROM BOARD
WHERE BOARD_NO = #{board_no}

SELECT COUNT(*) 
FROM BOARD
WHERE 1=1
	AND BOARD_TYPE = #{board_type} 
	
		AND UPPER(title) like '%' || UPPER( #{title} ) || '%'
	
	
		
			AND TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
